var React = require('react'),
	Select = require('react-select'),
	MoreList = require('../../../react-more-list/more-list.jsx'),
	PillSelector = require('../../../react-pill-selector/pill-selector.jsx'),
	RxAcc = require('../../../react-accordion/accordion.jsx');

var Accordion = RxAcc.Accordion,
	Section = RxAcc.Section,
	Heading = RxAcc.Heading,
	Content = RxAcc.Content;

var STATES = require('./data/states');

function logChange(value) {
	console.log('Select value changed: ' + value);
}

var CountrySelect = React.createClass({
	onClick: function() {
		this.props.onSelect(this.props.value);
	},
	render: function() {
		var className = this.props.value === this.props.selected ? 'active' : 'link';
		return <span onClick={this.onClick} className={className}>{this.props.children}</span>;
	}
});
 
var StatesField = React.createClass({
	getInitialState: function() {
		return {
			country: 'AU',
			selectValue: 'new-south-wales'
		}
	},
	switchCountry: function(newCountry) {
		console.log('Country changed to ' + newCountry);
		this.setState({
			country: newCountry,
			selectValue: null
		});
	},
	updateValue: function(newValue) {
		logChange('State changed to ' + newValue);
		this.setState({
			selectValue: newValue || null
		});
	},
	render: function() {
		var ops = STATES[this.state.country];
		return (
			<div>
				<label>States:</label>
				<Select options={ops} value={this.state.selectValue} onChange={this.updateValue} />
				<div className="switcher">
					Country:
					<CountrySelect value="AU" selected={this.state.country} onSelect={this.switchCountry}>Australia</CountrySelect>
					<CountrySelect value="US" selected={this.state.country} onSelect={this.switchCountry}>US</CountrySelect>
				</div>
			</div>
		);
	}
});
 
var RemoteSelectField = React.createClass({
	loadOptions: function(input, callback) {
		
		input = input.toLowerCase();
		
		var rtn = {
			options: [
				{ label: 'One', value: 'one' },
				{ label: 'Two', value: 'two' },
				{ label: 'Three', value: 'three' }
			],
			complete: true
		};
		
		if (input.slice(0,1) === 'a') {
			if (input.slice(0,2) === 'ab') {
				rtn = {
					options: [
						{ label: 'AB', value: 'ab' },
						{ label: 'ABC', value: 'abc' },
						{ label: 'ABCD', value: 'abcd' }
					],
					complete: true
				};
			} else {
				rtn = {
					options: [
						{ label: 'A', value: 'a' },
						{ label: 'AA', value: 'aa' },
						{ label: 'AB', value: 'ab' }
					],
					complete: false
				};
			}
		} else if (!input.length) {
			rtn.complete = false;
		}
		
		setTimeout(function() {
			callback(null, rtn);
		}, 500);
		
	},
	render: function() {
		return <div>
			<label>{this.props.label}</label>
			<Select asyncOptions={this.loadOptions} className="remote-example" />
		</div>;
	}
});

var sortAlpabetically = function(dataSet, sortField)
{
	dataSet.sort(function (a, b) {
	  if (a[sortField] > b[sortField]) {
	    return 1;
	  }
	  if (a[sortField] < b[sortField]) {
	    return -1;
	  }
	  // a must be equal to b
	  return 0;
	});
}

var sortReverseAlpabetically = function(dataSet, sortField)
{
	dataSet.sort(function (a, b) {
	  if (a[sortField] > b[sortField]) {
	    return -1;
	  }
	  if (a[sortField] < b[sortField]) {
	    return 1;
	  }
	  // a must be equal to b
	  return 0;
	});
}


var CustomList = React.createClass({
	mixins: [Select.CustomMenuMixin],
	getInitialState: function() { return {}; },
  buildSections: function(itemMap) {
  	var sections = [];

  	for(var sectionTitle in itemMap) {
  		var section = this.buildSection(itemMap[sectionTitle], sectionTitle);

  		sections.push(section)
  	}

  	return sections;
  },
  buildSection: function(sectionItems, sectionTitle) {
  	var pills = this.buildPillSelect(sectionItems, sectionTitle);

  	var content = this.buildContent(sectionItems, sectionTitle);

  	return (
  		<Section>
  			<Heading>{sectionTitle} ({sectionItems.length}) {pills}</Heading>
  			<Content>
  				{content}
  			</Content>
  		</Section>
  	)
  },
  buildPillSelect: function(moreListItems, sectionTitle) {
  	var self = this;
		var onPillSelect = function(id, sortFunction) {
			var newState = {};
			newState[sectionTitle] = sortFunction;

			self.setState(newState);
		};

  	return (
  		<PillSelector onItemClicked={onPillSelect} callClickOnLoad={true}>
  			<li data={sortAlpabetically}>A-Z</li>
  			<li data={sortReverseAlpabetically}>Z-A</li>
			</PillSelector>
  	);
  },
  buildContent: function(contentItems, sectionTitle) {
  	var categoryMapping = {};

  	contentItems.forEach(function(item) {
  		if(categoryMapping[item.faction] == undefined)
  		{
  			categoryMapping[item.faction] = {};
  		}
  		if(categoryMapping[item.faction][item.type] == undefined)
  		{
  			categoryMapping[item.faction][item.type] = [];
  		}

  		categoryMapping[item.faction][item.type].push(item);
  	});

  	var elements = [];

  	for(var faction in categoryMapping)
  	{
  		var subTypes = [];

  		for(var type in categoryMapping[faction])
  		{
  			var moreListItems = categoryMapping[faction][type];

		  	if(typeof this.state[sectionTitle] != 'undefined')
		  	{
		  		this.state[sectionTitle](moreListItems, "label");
		  	}

  			var moreList = this.buildMoreList(moreListItems);
  			subTypes.push(
  				<div className="sub-type subsection">
  					<span className="subsection-heading">{type}</span>
  					{moreList}
  				</div>
  			);
  		}

  		var element = (
  			<div className="sub-faction subsection">
  				<span className="subsection-heading">{faction}</span>
  				{subTypes}
  			</div>
  		);

  		elements.push(element);
  	}

  	return elements
  },
  buildMoreList: function(moreListItems) {
		var listItems = moreListItems.map(this.buildListItem, this);

		return (
			<MoreList>
				{listItems}
			</MoreList>
		)
  },
  buildListItem: function(listItem) {
			var className = this.props.focussedItem && this.props.focussedItem.value === listItem.value ? "is-focused" : null;

			var mouseDown = this.selectItem.bind(this, listItem);
			var mouseEnter = this.focusItem.bind(this, listItem);
			var mouseLeave = this.unfocusItem.bind(this, listItem);

			return <li className={className} onMouseDown={mouseDown} onMouseEnter={mouseEnter} onMouseLeave={mouseLeave}>{listItem.label}</li>
  },
	render: function() {
		var itemMap = {};

		this.props.filtered.forEach(function(item) {
			if(itemMap[item.side] == undefined) {
				itemMap[item.side] = [];
			}

			itemMap[item.side].push(item);
		});

		var sections = this.buildSections(itemMap);

		return (
			<Accordion>
				{sections}
			</Accordion>
		)
	}
});

